class Fader{constructor(time){this.time=time}fadeOut(element){this.fade(element,100,0,!1)}fadeIn(element){this.fade(element,0,100,!0)}fade(element,start,fin,show){var alpha=start,inc=fin>=start?2:-2;this.setDisplay(element,show);var timer=1e3*this.time/50,self=this,i=setInterval(function(){(inc>0&&alpha>=fin||inc<0&&alpha<=fin)&&clearInterval(i),self.setAlpha(element,alpha),alpha+=inc},timer);this.setDisplay(element,show)}setAlpha(targ,alpha){targ.style.filter="alpha(opacity="+alpha+")",targ.style.opacity=alpha/100}setDisplay(element,show){show?element.classList.remove("d-none"):setTimeout(function(){element.classList.add("d-none")},1e3*this.time)}}